import Add from './Add.js'

const Header = ({onAdd, showForm}) => {
    return (
        <header id="header">
            <h1>To-do App</h1>
            <Add color={showForm ? "#D63230" : '#1C77C3'} value={showForm ? <i className="fas fa-minus-circle"></i> : <i className="fas fa-plus-circle"></i>} onClick={onAdd}/>
        </header>
    )
}

export default Header
