import Add from './Add.js'

const Header = ({onAdd, showAdd}) => {
    return (
        <header id="header">
            <h1>To-do App</h1>
            <Add color={showAdd ? '#1C77C3' : "#D63230"} value={showAdd ? <i className="fas fa-plus-circle"></i> : <i className="fas fa-minus-circle"></i>} onClick={onAdd}/>
        </header>
    )
}

export default Header
