import Task from "./Task";

const tasks = [
  {
    id: 1,
    title: "Walk in the park",
    date: "Feb 16th",
  },
  {
    id: 2,
    title: "Feed the dogs",
    date: "March 21th",
  },
  {
    id: 3,
    title: "Work",
    date: "Aug 6th",
  },
];

const Tasks = () => {
  return (
    <section id="tasks">
      {tasks.map((task) => {
        <h3>{task.title}</h3>;
        <h4>{task.desc}</h4>
      })}
    </section>
  );
};

export default Tasks;
