import Task from "./Task"


const Tasks = ({tasks, onDelete, onToggle, onEdit, onAdd}) => {
  console.log(onAdd)
  console.log(onEdit)
  return (
    <section id="tasks">
      {tasks.map((task) => (
        <Task key={task.id} task={task} onDelete={onDelete} onToggle={onToggle} onEdit={onEdit} onAdd={onAdd}/>
      ))}
    </section>
  );
};

export default Tasks;
