import Add from "./Add.js";

const Header = ({ onAdd, showForm }) => {
  const buttonChange = () => {
    if (showForm) {
      return <i className="fas fa-minus-circle"></i>;
    } else {
      return <i className="fas fa-plus-circle"></i>;
    }
  };

  return (
    <header id="header">
      <h1>To-do App</h1>
      <Add
        color={showForm ? "#D63230" : "#1C77C3"}
        value={buttonChange}
        onClick={onAdd}
      />
    </header>
  );
};

export default Header;
