const Task = ({task, onDelete}) => {
  return (
    <div className="task">
      <div className="task-info">
        <h3 className="task-title">{task.title}</h3>
        <p className="task-desc">{task.date}</p>
      </div>
      <button className="remove-btn" onClick={() => onDelete(task.id)}>
        <i className="fas fa-times"></i>
      </button>
    </div>
  );
};

export default Task;
