import Task from "./Task"
import { useState } from 'react'

const tasks = [
  {
    id: 1,
    title: "Walk in the park",
    date: "Feb 16th",
  },
  {
    id: 2,
    title: "Feed the dogs",
    date: "March 21th",
  },
  {
    id: 3,
    title: "Work",
    date: "Aug 6th",
  },
];

const Tasks = () => {
    const [tasks, setTasks] = useState()

  return (
    <section id="tasks">
      {tasks.map((task) => (
        <h3 key={task.id}>{task.title}</h3>
      ))}
    </section>
  );
};

export default Tasks;
