import Header from './components/Header'
import Tasks from './components/Tasks'
import { useState } from 'react'

function App() {

  const [tasks, setTasks] = useState([
    {
      id: 1,
      title: "Walk in the park",
      date: "Feb 16th",
    },
    {
      id: 2,
      title: "Feed the dogs",
      date: "March 21th",
    },
    {
      id: 3,
      title: "Work",
      date: "Aug 6th",
    },
  ])

  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id))
  }

  return (
    <div className="container">
      <Header />
      {tasks.length > 0 ? <Tasks tasks={tasks} onDelete={deleteTask}/> : 'Add a task to start!'}
    </div>
  );
}

export default App;
