import Header from "./components/Header";
import Tasks from "./components/Tasks";
import { useState, useEffect } from "react";
import Form from "./components/Form";

function App() {
  const [showForm, setShowForm] = useState(false);
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const getTasks = async () => {
      const tasksFromServer = await fetchTasks();
      setTasks(tasksFromServer);
    };

    getTasks();
  }, []);

  const fetchTasks = async () => {
    const res = await fetch("http://localhost:5000/tasks");
    const data = await res.json();

    return data;
  };

  const fetchTask = async (id) => {
    const res = await fetch(`http://localhost:5000/tasks/${id}`);
    const data = await res.json();

    return data;
  };

  const deleteTask = async (id) => {
    await fetch(`http://localhost:5000/tasks/${id}`, { method: "DELETE" });

    setTasks(tasks.filter((task) => task.id !== id));
  };

  const addTask = async (task) => {
    const res = await fetch("http://localhost:5000/tasks", {
      method: "POST",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(task),
    });

    const data = await res.json();

    setTasks([...tasks, data]);
  };

  const toggleCompleted = async (id) => {
    const taskToToggle = await fetchTask(id);
    const updTask = { ...taskToToggle, completed: !taskToToggle.completed };
    const res = await fetch(`http://localhost:5000/tasks/${id}`, {
      method: "PUT",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(updTask),
    });

    const data = await res.json();

    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, completed: data.completed } : task
      )
    );
  };

  const editTask = async (id) => {
    var today = new Date();
    const taskToEdit = await fetchTask(id);
    const name = prompt("Enter a new name for the task");
    const date = prompt(
      "Enter a new date for the task",
      `${today.getFullYear()}-0${today.getMonth()}-${today.getDate()}`
    );

    const updTask = { ...taskToEdit, title: name, date: date };
    const res = await fetch(`http://localhost:5000/tasks/${id}`, {
      method: "PUT",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(updTask),
    });

    const data = await res.json();

    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, title: data.title, date: data.date } : task
      )
    );
  };

  const addType = () => {
    let select = ''
    if (!select){
    const header = document.getElementById("header")
    let select = document.createElement("select")
    select.className="select"
    select.innerHTML = `<option value="task" selected>Task</option>
    <option value="folder">Folder</option>`
    header.append(select)

    select.addEventListener("change", () => {
      if (select.value === 'task') {
        setShowForm(!showForm);
      }
    })}

    setShowForm(!showForm)

    if (!showForm){
      select.style.display = 'none'
    }
  };

  return (
    <div className="container">
      <Header onAdd={addType} showForm={showForm} />
      {showForm && <Form onAdd={addTask} />}
      {tasks.length > 0 ? (
        <Tasks
          tasks={tasks}
          onDelete={deleteTask}
          onToggle={toggleCompleted}
          onEdit={editTask}
        />
      ) : (
        "Add a task to start!"
      )}
    </div>
  );
}

export default App;
